	/**
	 * Test range.
	 */
	public void testRange() {
		for (int i = 130; i >= 80; i--) {
			Rank rank = new Rank(i);
			// System.err.println(rank);
			assertTrue("" + rank + "/" + i + "/" + rank.getRank(),
					Rank.convert(rank.toString()) == i);
		}
	}

	/**
	 * Test ratings.
	 */
	public void testRatings() {
		for (int i = 130; i >= 80; i--) {
			Rank rank = new Rank(i);
			double d = rank.getRating();
			assertTrue(d >= 0.0);
			assertTrue(d <= 1000);
			// System.err.println(rank);
			assertTrue("" + rank + "/" + i + "/" + rank.getRank(),
					Rank.convert(rank.toString()) == i);
		}
	}

